create table movie
(
    pk_movie_id  bigint auto_increment
        primary key,
    name         varchar(45)  not null,
    year         int          not null,
    running_time int          not null,
    banner_link  varchar(255) not null,
    about        text         null
);

INSERT INTO mydb.movie (name, year, running_time, banner_link, about) VALUES ('Dune', 2021, 155, 'http://localhost:8080/images/dune.jpg', 'Kultovní sci-fi dílo vypráví o mocenských bojích uvnitř galaktického Impéria, v nichž jde o ovládnutí planety Arrakis: zdroje vzácného koření - melanže, jež poskytuje zvláštní psychické schopnosti, které umožňují cestování vesmírem. Padišáh imperátor svěří správu nad Arrakisem a s ní i komplikovanou těžbu neobyčejné látky vévodovi rodu Atreidů. Celá anabáze je ale součástí spiknutí, z něhož se podaří vyváznout jen vévodovu synovi Paulovi a jeho matce, kteří uprchnou do pouště. Jejich jedinou nadějí jsou nedůvěřiví domorodí obyvatelé fremeni, schopní trvale přežít ve vyprahlé pustině. Mohl by Paul Atreides být spasitelem, na kterého tak dlouho čekají?');
INSERT INTO mydb.movie (name, year, running_time, banner_link, about) VALUES ('Venom', 2018, 112, 'http://localhost:8080/images/venom.jpg', 'Na filmová plátna přichází jedna z nejlepších a nejkomplexnějších comicsových postav společnosti Marvel. Novinář Eddie Brock (Tom Hardy) se stává hostitelem mimozemského symbionta Venoma. Brock se v rámci své práce novináře pokouší již po celé roky odhalit veřejnosti skutečnou tvář známého vědce společnosti Life Foundation Carltona Drakea (Riz Ahmed) – jeho odhodlání se přerodilo v posedlost, která mu zlikvidovala kariéru a zničila vztah s jeho přítelkyní Anne (Michelle Williams). Když do svého těla Eddie vstřebá jeden z Drakeových experimentů – mimozemšťana Venoma – zničehonic zjišťuje, že disponuje novými superschopnostmi a má možnost dělat, cokoliv se mu zachce. Zvrhlý, temný, nepředvídatelný a zuřivý Venom má za následek, že se Eddie zoufale pokouší udržet pod kontrolou své nebezpečné schopnosti, které ho současně velice přitahují a opájejí. Jelikož Eddie a Venom navzájem jeden druhého potřebují k dosažení svých cílů, jejich vzájemné pouto a propojení se prohlubuje – až přestává být jasné, kde vlastně končí Eddie a začíná Venom.');
INSERT INTO mydb.movie (name, year, running_time, banner_link, about) VALUES ('Star Wars: Epizoda III - Pomsta Sithů', 2005, 140, 'http://localhost:8080/images/star_wars_3.png', 'Válka mezi povstalci a Republikou pokračuje. Generálovi Grievousovi se podařilo unést kancléře Palpatinea a rytíři Jedi, Obi-wan Kenobi a Anakin Skywalker se vydávají na palubu Grievousovy lodi kancléře osvobodit. Poté, co vniknou na loď, narazí na hraběte Dooku. Dojde mezi nimi k souboji, ve kterém Anakin Dookua porazí, a chystá se ho zajmout. Ještě svázaný Palpatine však Anakina přemluví, aby Dookua na místě zabil. Je to něco, co by správný Jedi neměl nikdy udělat. Anakin však přemlouvání podlehne. I když rytíři Jedi Palpatina zachránili, jsou vzápětí znovu zajati vojáky generála Grievouse. Ten ale rytíře Jedi podcenil, a tak se situace brzy obrátí a je to právě generál, kdo z lodi, kterou opět ovládli Jediové, musí prchnout v záchranném modulu. Anakin rozpadající se Grievousovu loď přivede bezpečně na letiště a rázem se stane hrdinou a tím, kdo zachránil kancléře. Kancléř zahrnuje Anakina svým přátelstvím a ostatní Jediové cítí, že něco není v pořádku. Anakina pronásledují zlé sny, v nich se mu zdá, jak jeho milovaná Padmé (Amidala) umírá při porodu jejich dětí. Anakin se se svými obavami obrátí na Yodu se žádostí o pomoc. Yodovy rady mu však nepomohou a ještě více posílí Anakinovy pochybnosti ohledně chování Jediů, kteří po něm chtějí, aby jim donášel informace o chování kancléře. Dalším šokem pro Anakina je zjištění, že i Padmé pochybuje o současném fungování Republiky. Anakin se stává jasným spojencem Palpatinea a ten se mu přizná, že je tajným lordem Sithů, a nabízí mu přejít na stranu Temné síly. Anakin to odmítá a informuje představeného řádu Jediů, mistra Windu. Ten rozhodne, že Palpatine musí být zatčen. Nerozumně však odmítne, aby se Anakin zatčení zúčastnil. Stane se tak ve chvíli, kdy Obi-Wan vede vojska proti zbytkům sil generála Grievouse. Windu jde s několika Jedii zatknout Palpatinea. Zatím je Anakin plný pochybností a začne se bát, že pokud Palpatine zahyne, nebude Anakin schopen zachránit před smrtí Padmé. Právě Palpatine slíbil Anakinovi možnost pomocí Temné síly uchránit Padmé před její smrtí. Anakin se rozhodne a spěchá do senátu zabránit zničení Palpatinea. Tady zatím dojde k boji mezi Palpatinem a Jedii. Palpatine zabije všechny Jedie mimo mistra Windu. Když se mistr Windu chystá zabít Palpatinea, Anakin mu v tom zabrání a shodou neštěstí zabije Windu. Následně se přidává na stranu Palpatinea, kterému boj změnil tvář do hrozného šklebu. Anakin se stává dobrovolným služebníkem Palpatinea a ten ho nazve novým jménem, Darth Vaderem. Palpatine se v senátu prohlašuje za císaře a vojákům nařizuje splnit rozkaz 66. Tím je likvidace všech Jediů, kteří by mohli jeho plány zhatit. Darth Vader, bývalý Anakin, se zabíjení Jediů zúčastní, a dokonce zabíjí i malé děti, žáky Jediů. Obi-Wanovi a mistrovi Yodovi se podaří smrti uniknout a pokusí se proti císaři, bývalému senátorovi Palpatineovi, zasáhnout. Yoda však v souboji neuspěje a musí utéct do vyhnanství');
